--
-- Структура таблицы `experts`
--

CREATE TABLE `experts` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `list_expert_id` int(11) NOT NULL,
  `education` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `public` text COLLATE utf8mb4_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Дамп данных таблицы `experts`
--

INSERT INTO `experts` (`id`, `list_expert_id`, `education`, `public`) VALUES
(1, 1, 'Кандидат юридичних наук, доцент кафедри кримінального права № 1 Національного юридичного університету ім. Ярослава Мудрого (м. Харків), адвокат. Експерт в галузі права, сфера наукових інтересів – правовий статус штучного інтелекту (Artificial Intelligence) та правовідносини, що пов’язані з цифровою людиною (Digital human being, Homo numeralis, Homo digitalis, або Homo horologium). Автор понад 100 публіцистичних та наукових робіт (тез доповідей, статей, розділів підручників, монографій, методичних посібників тощо). ', ''),
(2, 2, 'Науковець-практик в галузі будівництва.  Академік Академії будівництва України (АБУ). Експерт в сфері містобудування та будівельних інновацій. Директор науково-технічного центру АБУ. Керівник «Міжгалузевого науково-виробничого кластеру інноваційного будівництва». Автор понад 100 публіцистичних та наукових статей. Кандидат технічних наук, викладач Київського національного університету будівництва і архітектури. Радник Голови Комітету ВРУ IX скликання з питань організації державної влади, місцевого самоврядування, регіонального розвитку та містобудування. Автор методології впровадження в Україні принципів Нового урбанізму.', ''),
(3, 3, 'Економіст, громадський діяч, публіцист. Експерт в сфері економічної та індустріальної політики, доктор економічних наук. Автор понад 150 публіцистичних та наукових статей, автор та співавтор законопроектів. Директор «Агентства розвитку Дніпра». Керівник наукових програм Інституту суспільних досліджень Почесний директор ІСД.', '•	50 відтінків самолегітимації. Від економічного націоналізму до економічного патріотизму. – К.: Аграр Медіа Груп, 2018. – 280 с.\r\n•	Держава біля керма: теорія та практика українського економічного націоналізму (XX – початку XXI ст.): монографія. – Дніпро : Герда, 2017. – 340 с.\r\n•	Формування економічної платформи українського націоналістичного руху у 1920-1950-х роках. – Д.: Свидлер А.Л., 2009. – 304 с.\r\n•	Ідеологія сучасного українського націоналізму: економічний аспект. – Д.: Акцент ПП, 2012. – 104 с.\r\n•	Економічна стратегія ОУН 1920-1950-ті: розробка та реалізації. – К., 2011, – 80 с.\r\n•	Кубань в уявній географії українських націоналістичних інтелектуалів 20-40-х років XX ст. // Козацька спадщина: альманах. – Київ. – 2008. – Вип. 4.\r\n•	Українське національне харчування: минуле і майбутнє. Уроки здоров’я. – Д.: Герда, 2009. – 234 с.\r\n'),
(4, 4, 'Економіст-міжнародник. Експерт в сфері міжнародної економічної політики, доктор економічних наук. Автор понад 150 наукових та публіцистичних статей. Професор Інституту міжнародних відносин Київського національного університету імені Тараса Шевченка.', ''),
(5, 5, 'Економіст, експерт в сфері зовнішньоекономічної діяльності, кандидат економічних наук, доцент. Автор понад 100 наукових статей. Завідувач кафедри економіки та менеджменту зовнішньоекономічної діяльності Національної академії статистики, обліку та аудиту. ', ''),
(6, 6, 'Економіст, експерт у сфері логістики, доктор філософії в галузі економіки. Автор понад 50 наукових статей. Старший викладач кафедри економіки та менеджменту зовнішньоекономічної діяльності Національної академії статистики, обліку та аудиту.', ''),
(7, 7, 'Економіст-міжнародник, експерт в сфері міжнародної трудової міграції та регіональної міграційної політики, доктор економічних наук. Автор понад 150 наукових статей. Доцент Інституту міжнародних відносин Київського національного університету імені Тараса Шевченка.', ''),
(8, 8, 'Економіст-міжнародник, експерт в сфері інноватики, «економіки знань», міжнародних стандартів фінансової звітності та міжнародного фінансового менеджменту. Автор понад 80 науково-методичних праць. Доцент кафедри міжнародного бізнесу Інституту міжнародних відносин Київського національного університету ім.                         Тараса Шевченка. Координаторка Центру Кар’єри ІМВ КНУ імені Тараса  Шевченка.', ''),
(9, 9, 'Кандидат історичних наук, доцент кафедри історії України Дніпровського національного університету ім. Олеся Гончара.\r\nВиступав організатором «круглих столів» та брав участь у якості експерта в прес-конференціях, присвячених дражливим питанням української історії (дискусія навколо передатування м. Дніпропетровськ, козацько-єврейські стосунки, Друга світова війна та український визвольний рух, історія УПА тощо).\r\nЗагалом автор понад 50 наукових статей. Член редколегії збірників наукових праць «Наддніпрянська Україна: історичні процеси, події, постаті» та «Січеславський альманах».', ''),
(10, 10, 'Філолог, журналіст, громадський діяч.\r\nЯк журналіст співпрацює з газетами «Лица», «Народна армія», «Україна молода», «Днепр вечерний», інтернет-ресурсами: «Експедиція 21», «ГРАД».\r\nАвтор великої кількості газетних матеріалів з історії України, з психології та етнопсихології, а також присвячені світоглядним проблемам постколоніального суспільства та «суспільства споживання».', ''),
(11, 11, 'Працював асистентом кафедри історії України ДНУ ім. Олеся Гончара (2015 р.). Зараз аспірант Дніпровського національного університету імені Олеся Гончара.\r\nПризер всеукраїнських студентських олімпіад та турнірів з історії; автор проектів, які здобували наукові гранти; учасник чисельних конференцій та семінарів; автор низки наукових публікацій.', ''),
(12, 12, 'Аспірантка Дніпровського національного університету імені Олеся Гончара.\r\nПрактикуючий викладач історії.\r\nАвтор ряду наукових публікацій, учасниця міжнародних та Всеукраїнських конференцій та семінарів.', ''),
(13, 13, 'Очолював науковий комітет у складі Студентської Ради факультету в 2016-2017 рр. Автор та один з ідейних натхненників медіа-проекту “Етажерка” (онлайн платформа для авторських курсів лекцій викладачів у галузі гуманітарних студій).\r\nУчасник Всеукраїнських конференцій, автор наукових статей і тез.', ''),
(14, 14, 'Докторант Єврейського університету в Єрусалимі (The Hebrew University of Jerusalem).\r\nУчасник культурно-просвітницького молодіжного руху «Культурна революція. Експеримент».\r\nСфера зацікавлень: Ідеологічні дискурси в ЗМІ, фреймінг, міфотворення та формування національних ідентичностей, вплив пропаганди та рецепція ідеологічних наративів під час конфлікту.', ''),
(18, 18, 'Економіст. Працював в банківських, страхових та інвестиційних організаціях.\r\nСфера фахових інтересів: зовнішньоекономічна політика; інвестиційна політика; ринок капіталів.', ''),
(19, 19, 'Сергій Корсунський – дипломат, науковець. Доктор фізико-математичних наук. Директор Дипломатичної Академії імені Геннадія Удовенка при МЗС України. Надзвичайний і повноважний посол України. Заслужений економіст України. З 14 квітня 2020 року – посол України в Японії. ', ''),
(20, 20, 'Денис Гурак – експерт з питань міжнародної політики та безпеки. Старший науковий співробітник The Potomaс Foundation. Екс-заступник генерального директора з зовнішньоекономічної діяльності концерну \"Укроборонпром\". Виконавчий директор асоціації «Україна-Франція»', ''),
(21, 21, 'Олександр Данилюк – український правозахисник, громадський діяч. Лідер та координатор громадського руху «Спільна справа». Екс-радник міністра оборони України. Експерт з питань безпеки та оборони. Науковий співробітник The Potomaс Foundation. Керівник Центру оборонних реформ.', '');

